mapper.select:
select * from (
select rownum as r_num, a.* from (
select * from notice where bname = #{bname} and status = 0
order by groups DESC, lev ASC
) a
) where r_num between #{begin} and #{end}

mapper.update:
update notice set
title = #{title},
content = #{content},
regdate = sysdate

	,uploadFileName = #{uploadFileName}

where seq = #{seq} AND pwd = #{pwd}

UPDATE notice
SET status = 1
WHERE seq = #{seq}
